import React from "react";
import "./event.css";


export function Events(){
    return(
      <div>
        <div className="event">
        <h2>Upcoming Events</h2>
        <div className="section">
            <h3>Webnar</h3>
            <h4>July 11|Virtual</h4>
            <img src="./img/meet.png"/>
            <p>We will be talking about technologies that have come up in this 21st century.<br></br>The different technologies for communication as a tech community</p>
           <button className="next">Register</button>
        
        </div>

        <div>
            <h3>Cyber Security</h3>
            <h4>July 15|Virtual</h4>
            <img src="./img/meet.png"/>
            <p>Cyber Security is akey aspect for our information, we shall go deep to explore this topic.<br></br>Cookies are not safe!!, therefore sessions were intoduced. Come and know more</p>
            <button className="next">Register</button>
        </div>

        <div>
            <h3>Google's Coding Competitios</h3>
            <h4>March 13|virtual</h4>
            <img src="./img/meet.png"/>
            <p>Come be the Code Queen/King you dreamed of. Come give us the best of you<br></br> Be an inspiration to young programmers out there</p>
            <button className="next">Register</button>
        </div>

        </div>


       <div className="section">
        <div>
            <h3>Google for Startups Accelerator UK</h3>
            <h4>Applications are open till May 20</h4>
            <img src="./img/meet.png"/>
            <p>Great opportunity for supporting technology startups that are in the feild of AI and Ml</p>
            <button className="next">Register</button>
        </div>
       

        <div>
           <h3>Artificial Intelligence</h3>
            <h4>June 7|Virtual</h4>
            <img src="./img/meet.png"/>
            <p>We will be talking about technologies that have come up in this 21st century.<br></br>The different technologies for communication as a tech community</p>
           <button className="next">Register</button>
        
        </div>


        <div>
            <h3>Women in tech</h3>
            <h4>Sept  11|Virtual</h4>
            <img src="./img/meet.png"/>
            <p>We will be talking about technologies that have come up in this 21st century.<br></br>The different technologies for communication as a tech community</p>
           <button className="next">Register</button>
        
        </div>
        </div>


      </div>





    )

}
